                abi.encode(x1, y1, x2, y2)
            );
            require(success, "ecadd failed");
            (x1, y1) = abi.decode(ret, (uint256, uint256));
        }

        if (y1 % 2 == 1) x1 ^= 1 << 254;

        return bytes32(x1);
    }

    /// @dev Constructs a position ID from a collateral token and an outcome collection. These IDs are used as the ERC-1155 ID for this contract.
    /// @param collateralToken Collateral token which backs the position.
    /// @param collectionId ID of the outcome collection associated with this position.
    function getPositionId(IERC20 collateralToken, bytes32 collectionId)
        internal
        pure
        returns (uint256)
    {
        return
            uint256(keccak256(abi.encodePacked(collateralToken, collectionId)));
    }
}

contracts/Counter.sol.content:
pragma solidity >=0.6.0 <0.7.0;

import "@nomiclabs/buidler/console.sol";

contract Counter {
    uint256 public count;
    address private name;

    constructor() public {
        count = 0;
        name = msg.sender;
        console.log("Current Count", count);
        console.log("Current Owner", name);
    }

    // Function to get the current count
    function get() public view returns (uint256) {
        console.log("Current Count", count);
        console.log("Current Owner", name);
        return count;
    }

    // Function to increment count by 1
    function inc() public {
        count += 1;
    }

    // Function to decrement count by 1
    function dec() public {
        count -= 1;
    }
}

