// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.6.0 <0.7.0;
import "@openzeppelin/contracts/utils/Counters.sol";

contract ProjectTrackerFactory {
    using Counters for Counters.Counter;
    Counters.Counter public nonce; // acts as unique identifier for minted NFTs

    ProjectNegotiationTracker[] public projects;

    event ProjectCreated(address tokenAddress);

    mapping(string => uint256) public nameToProjectIndex;

    function deployNewProject(
        address _owner,
        string memory _name,
        string memory _milestones,
        uint256[] memory _timeline,
        uint256[] memory _budgets
    ) public returns (address) {
        //need to check if name or symbol already exists
        require(nameToProjectIndex[_name] == 0, "Name has already been taken");
        ProjectNegotiationTracker newProject = new ProjectNegotiationTracker(
            _owner,
            _name,
            _milestones,
            _timeline,
            _budgets
        );
        projects.push(newProject);

        nonce.increment(); //start at 1
        nameToProjectIndex[_name] = nonce.current();

        emit ProjectCreated(address(newProject));

        //should create safe in here too, and add an address variable for the safe.
        return address(newProject);
    }

    function getProject(string memory _name)
        public
        view
        returns (address projectAddress, string memory name)
    //uint256[] memory _timeline,
    //uint256[] memory _budgets
    {

            ProjectNegotiationTracker selectedProject
         = projects[nameToProjectIndex[_name] - 1];

        return (
            address(selectedProject),
            selectedProject.projectName()
            //selectedProject.milestoneTimelineMonths(),
            //selectedProject.milestoneBudget()
        );
    }
}

contract ProjectNegotiationTracker {
    address public owner;
    bool public ownerApproval = false;
    string public projectName;
    string public milestones;
    address public winningBidder;
    uint256[] public milestoneTimelineMonths;
    uint256[] public milestoneBudget;

    event currentTermsApproved(address approvedBidder);

    constructor(
        address _owner,
        string memory _name,
        string memory _milestones,
        uint256[] memory _timeline,
        uint256[] memory _budgets
    ) public {
        owner = _owner;
        projectName = _name;
        milestones = _milestones;
        milestoneTimelineMonths = _timeline;
        milestoneBudget = _budgets;
    }

    mapping(address => uint256[]) public BidderToTimeline;
    mapping(address => uint256[]) public BidderToBudgets;
    mapping(address => bool) public BidderProposalStatus;

    function newBidderTerms(
        uint256[] calldata timeline,
        uint256[] calldata budgets
    ) external {
        require(
            ownerApproval == false,
            "another proposal has already been accepted"
        );
        require(msg.sender != owner, "owner cannot create a bid");
        BidderToTimeline[msg.sender] = timeline;
        BidderToBudgets[msg.sender] = budgets;
        BidderProposalStatus[msg.sender] = false;
    }

    function loadBidderTerms(address _bidder)
        external
        view
        returns (uint256[] memory timeline, uint256[] memory budgets)
    {
        require(
            msg.sender == owner,
            "Only project owner can see proposed terms"
        );
        return (BidderToTimeline[_bidder], BidderToBudgets[_bidder]);
    }

    function approveBidderTerms(address _bidder) external {
        require(msg.sender == owner, "Only project owner can approve terms");
        ownerApproval = true;
        BidderProposalStatus[_bidder] = true;
        winningBidder = _bidder;
        emit currentTermsApproved(_bidder);
    }
}

contracts/SecurityTokenERC20.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.6.0 <0.7.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@nomiclabs/buidler/console.sol";

contract SecurityToken is ERC721, AccessControl, ReentrancyGuard {
    using Counters for Counters.Counter;
    using SafeMath for uint256;
    using Address for address payable;

    event newFunder(address funder, uint256 value, uint256 tenor);

    IERC20 public ERC20token; //rinkeby, may need to adjust for USDC
    string public projectName;
    string public projectSymbol;
    address public owner;
    address public bidder;
    address public holderContract;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE"); //use bytes as it is stored in a single word of EVM, string is dynamically sized (and can't be returned from a function to a contract)
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    bytes32 public constant AUDIT_ROLE = keccak256("AUDIT_ROLE");

    Counters.Counter public nonce; // acts as unique identifier for minted NFTs

    constructor(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        address _ERC20token,
        address projectOwner,
        address projectBidder,
        address auditors
    ) public ERC721(projectName, projectSymbol) {
        projectName = _name;
        projectSymbol = _symbol;
        owner = projectOwner;
        bidder = projectBidder;
        ERC20token = IERC20(_ERC20token);
        _setBaseURI(baseURI);
        _setupRole(DEFAULT_ADMIN_ROLE, projectOwner);
        _setupRole(MINTER_ROLE, projectBidder);
        _setupRole(BURNER_ROLE, projectBidder);
        _setupRole(AUDIT_ROLE, auditors);
    }

    struct FundingToken {
        address projectFunder;
        uint256 fundingValue;
        uint256 tenor;
    }

    mapping(uint256 => FundingToken) public IDtoToken;
    FundingToken[] public fundedTokens;

    function setHolder(address _holder) external {
        require(msg.sender == owner, "only owner can set escrow");
        holderContract = _holder;
    }

    /*
     * @notice Assigns funding properties: Project Funder, Funding Value, Tenor of funding to the tokenID
     * @dev Creates TokenProperties struct with list of token properties and assigns it to a tokenID via
     * tokenIDToProperties mapping
     * @param _projectFunder the address of the funder of this particular project
     * @param _fundingValue uint that specifies the amount of funding for the project
     * @param _tenor uint specifying the tenor of the funding
     * @param _tokenID uint with the ID of the token to be assigned the above properties
     */
    function createFunding(
        address _projectFunder,
        uint256 _fundingValue,
        uint256 _tenor
    ) internal {
        fundedTokens.push(FundingToken(_projectFunder, _fundingValue, _tenor));
    }

    //gets called by buyOne (I guess AAVE can just buyone too?)
    function mint(
        address _projectFunder,
        uint256 _fundingValue,
        uint256 _tenor
    ) internal {
        uint256 tokenId = nonce.current();
        nonce.increment(); //Note that ID starts at 0.

        createFunding(_projectFunder, _fundingValue, _tenor); //can get funding details by calling tokenID in fundedTokens[], match to owner with tokenOfOwnerByIndex
        _safeMint(_projectFunder, tokenId);

        IDtoToken[tokenId] = fundedTokens[tokenId];

        emit newFunder(
            fundedTokens[tokenId].projectFunder,
            fundedTokens[tokenId].fundingValue,
            fundedTokens[tokenId].tenor
        );
    }

    function recieveERC20(address _sender, uint256 _value) internal {
        ERC20token.transferFrom(_sender, address(this), _value); //actual transfer
    }

    function approveERC20toHolder(uint256 _value) internal {
        ERC20token.approve(holderContract, _value); //approve transfer
    }

    //don't need exchange? not sure what best practice is.
    function buyOne(uint256 _fundingValue, uint256 _tenor)
        external
        payable
        nonReentrant
    {
        recieveERC20(msg.sender, _fundingValue);
        approveERC20toHolder(_fundingValue);
        mint(msg.sender, _fundingValue, _tenor);
    }
}

